function ViewCollection(n){let t=$(n).closest("[name='Collection']").attr("id");location.href=location.origin+"/Collection?id="+t}function Delete(){let n=$(this).closest("[name='Collection']").attr("id");sendRequest("Delete",{collectionID:n})}function Edit(){let n=$(this).closest("[name='Collection']").attr("id");location.href=location.origin+"/Collection/Create?id="+n}function onToggleDetail(n){let t=$(n).closest("[name='Collection']");t.hasClass("expand-collection")?(t.removeClass("expand-collection"),$(n).html("<i class='fa fa-arrow-down'><\/i>")):(t.addClass("expand-collection"),$(n).html("<i class='fa fa-arrow-up'><\/i>"))}function sendRequest(n,t){$.ajax({type:"POST",url:"/Collection/Manage/"+n,headers:{RequestVerificationToken:$('input[name="__RequestVerificationToken"]').val()},data:t}).done(function(){location.reload()})}let descriptions=$("[name='Description']"),markdown=window.markdownit();$("[name='colDelete']").on("click",Delete);$("[name='colEdit']").on("click",Edit);descriptions.each(function(n,t){t=$(t);let i=t.html(),r=markdown.render(i);t.html(r)});