function setTheme(n,t){document.getElementById(t).setAttribute("disabled","true");localStorage.setItem("theme",n);document.getElementById(n).removeAttribute("disabled")}function toggleTheme(n){n.checked?setChecked():removeChecked();localStorage.getItem("theme")==="theme-dark"?setTheme("theme-light","theme-dark"):setTheme("theme-dark","theme-light")}function setChecked(){document.getElementsByName("themeBtn").forEach(function(n){n.checked=!0})}function removeChecked(){document.getElementsByName("themeBtn").forEach(function(n){n.checked=!1})}localStorage.getItem("theme")==="theme-dark"?setTheme("theme-dark","theme-light"):setTheme("theme-light","theme-dark");window.addEventListener("load",function(){localStorage.getItem("theme")==="theme-dark"?removeChecked():setChecked()});