function setTheme(n,t){document.getElementById(t).setAttribute("disabled","true");localStorage.setItem("theme",n);document.getElementById(n).removeAttribute("disabled")}function toggleTheme(){localStorage.getItem("theme")==="theme-dark"?setTheme("theme-light","theme-dark"):setTheme("theme-dark","theme-light")}function setChecked(n){n&&document.getElementById("themeBtn").setAttribute("checked",!0)}localStorage.getItem("theme")==="theme-dark"?setTheme("theme-dark","theme-light"):setTheme("theme-light","theme-dark");window.addEventListener("load",function(){localStorage.getItem("theme")==="theme-dark"?setChecked(!1):setChecked(!0)});